
# Mandelbrot fractal locations of interest, stored with very high precision

# high precision location storage

struct Location
    center :: Complex{BigFloat}
    radius :: Real
end

# added context for efficient and nice looking renders

struct LocationContext
    location :: Location
    iterations :: Integer
    extendedrange :: Bool
    referenceprecision :: Int
end

function LocationContext(
        real_part :: String, 
        imag_part :: String, 
        radius :: Real, 
        iterations :: Integer, 
        extendedrange :: Integer = radius isa ExtendedRangeFloat,
        referenceprecision :: Integer = 2048)

    LocationContext(
        Location(
            parse(BigFloat, real_part) + parse(BigFloat, imag_part)im,
            radius),
        iterations,
        extendedrange,
        referenceprecision)
end

